import { StatusBar } from "expo-status-bar";
import React, { useRef, useState } from "react";
import { WebView } from "./node_modules/react-native-webview";
import {
  ImageBackground,
  StyleSheet,
  Image,
  Text,
  View,
  TouchableOpacity,
  Button,
  SafeAreaView,
} from "react-native";
import bgImg from "./assets/background.jpg";
import * as ImagePicker from "./node_modules/expo-image-picker";
import logo from "./assets/zenLogo.jpeg";


let op = async () => {
  let permission = await ImagePicker.getCameraPermissionsAsync();
  if (permission.granted === false) {
    alert("Permission to access camera roll is required!");
    return;
  }
  let pickerResult = await ImagePicker.launchImageLibraryAsync();
  console.log(pickerResult);
};


export default class App extends React.Component {
  webview = null;

  onNavStateChange = (NavState) => {
    console.log(NavState);

  }

  render() {
    return (
      <SafeAreaView style={ styles.MainContainer }>
        <WebView
        ref={(ref) => {
          (this.webview = ref);
        }}
        source={{ uri: 'https://zenspanail.com/' }}
        onNavigationStateChange={this.onNavStateChange} >
        
        </WebView>
        <View style={styles.menu}>
          <Image source={logo}></Image>
        </View>
      </SafeAreaView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex:1,
    marginTop:8
  },
  menu: {
    height:100,
    backgroundColor:"white",
    shadowColor:"black",
    shadowOffset: {
      width:2,
      height:8
    },
    shadowOpacity: 0.1,
    shadowRadius:2,
  },
  LogoContainter: {
    flex: 1,
    height: 200,
  },
  logo: {
    width: 180,
    height: 180,
    marginLeft: 10,
  },
  MainContainer: {
    flex: 1,
    backgroundColor: "green",
    flexDirection:"column"
  },
});
