import { StatusBar } from "expo-status-bar";
import React from "react";
import { WebView } from "./node_modules/react-native-webview";
import {
  ImageBackground,
  StyleSheet,
  Image,
  Text,
  View,
  TouchableOpacity,
  Button,
  SafeAreaView,
} from "react-native";
import bgImg from "./assets/background.jpg";
import * as ImagePicker from "./node_modules/expo-image-picker";
import { Test } from "./src/components/Test/Test";
import logo from "./assets/zenLogo.jpeg";
import { Ionicons , FontAwesome5 } from '@expo/vector-icons';
export default class App extends React.Component {
  constructor(props) {
    super(props);
    this.state  = {
      count : 0,
      url : "https://zenspanail.com"
    }
  }
  op = async () => {
    let permission = await ImagePicker.getCameraPermissionsAsync();
    if (permission.granted === false) {
      alert("Permission to access camera roll is required!");
      return;
    }
    let pickerResult = await ImagePicker.launchImageLibraryAsync();
    console.log(pickerResult);
  };

  increaseCount() {
    this.setState( {count: this.state.count += 1})
 }

  homePage(){
    this.setState({ url : "https://google.com"})
  }

  render(){
    return (
      <SafeAreaView style={{backgroundColor:"green", flex:1,flexDirection:"column"}}>
           <WebView
             source={{
               uri: "https://zenspanail.com",
             }}
             style={styles.container}
           >
           </WebView>
           <View style={styles.menu}>
             <TouchableOpacity onPress = { this.homePage.bind(this) }>
               <Image source={logo} style={styles.logo}></Image>
             </TouchableOpacity>
             <FontAwesome5 name="user-circle" size={35} color="#80a831" /> 
             {/* <Ionicons name="md-checkmark-circle" size={35} color="#80a831" />  */}
             <TouchableOpacity onPress = { this.increaseCount.bind(this)}>
               <Text> Press me an see. {this.state.count}</Text>
             </TouchableOpacity>
           </View>
       </SafeAreaView>
       );
  }
}
const styles = StyleSheet.create({
  logo: {
    width: 80,
    height: 60,
    alignSelf:"flex-start"
  },
  container: {
    flex:1,
  },
  menu:{
    flex:0.1,
    flexWrap:"wrap",
    flexDirection:"row",
    justifyContent:"space-around",
    backgroundColor:"white",
    shadowColor:"black",
    height:120,
    shadowOffset:{
      width:2,
      height:3,
    },
    alignItems:"center",
    shadowOpacity:5,
    shadowRadius:10,
    paddingRight:10,
  }
});
